import React, { Component } from 'react'; 
import { Layout, Row, Col, Table } from 'antd'; 
import '../assets/css/portofolio.css' 
import Navbar from '../common/layout/navbar-landing' 
import ButtonHome from '../common/component/button/button-home'; 

const { Content } = Layout; // membuat konstanta content 
const { Option } = Select;
const dateNow = moment().format('YYYY-MM-DD');
const { confirm } = Modal;

class KmsComponent extends Component{

    state = { 
        waitingSertifikat : [],
        penandatangan : [],
        id_sertifikat:'',
        id_penandatangan : undefined,
        loading : false,
        visible : false,
    }

    componentDidMount(){
        this.getCertificateAdmin();
    }

    render(){
        const { initialData, data, columns } = this.props;

        getColumnSearchProps = dataIndex => ({
            filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
              <div style={{ padding: 8 }}>
                <Input
                  ref={node => {
                    this.searchInput = node;
                  }}
                  placeholder={`Search ${dataIndex}`}
                  value={selectedKeys[0]}
                  onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
                  onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
                  style={{ width: 188, marginBottom: 8, display: 'block' }}
                />
                <Button
                  type="primary"
                  onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
                  icon="search"
                  size="small"
                  style={{ width: 90, marginRight: 8 }}
                >
                  Search
                </Button>
                <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
                  Reset
                </Button>
              </div>
            ),
            filterIcon: filtered => (
              <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
            ),
            onFilter: (value, record) =>
              record[dataIndex]
                .toString()
                .toLowerCase()
                .includes(value.toLowerCase()),
            onFilterDropdownVisibleChange: visible => {
              if (visible) {
                setTimeout(() => this.searchInput.select());
              }
            },
            render: text =>
              this.state.searchedColumn === dataIndex ? (
                <Highlighter
                  highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
                  searchWords={[this.state.searchText]}
                  autoEscape
                  textToHighlight={text.toString()}
                />
              ) : (
                text
              ),
        });

        handleSearch = (selectedKeys, confirm, dataIndex) => {
            confirm();
            this.setState({
              searchText: selectedKeys[0],
              searchedColumn: dataIndex,
            });
        };

        handleReset = clearFilters => {
            clearFilters();
            this.setState({ searchText: '' });
        };

        getCertificateAdmin=()=>{
            this.setState({loading: true})
            API.get(`/admin/sertifikat-waiting`)
            .then(res => {
                this.setState({loading: false})
                console.log('res',res)
                this.setState({waitingSertifikat:res.data.data.sertifikat})
            });
        }

        handlePenandatangan = (input, option) => {
            console.log('input', input, 'option', option);
            this.setState({ id_penandatangan: input })  
        }

        return(
            <Layout className="landing-container">
                <Content style={{ 
                    overflow: "hidden",
                    padding:15,
                    minHeight:280,
                    borderRadius:"8px",
                    }}
                > 
                    <Row className="section-container">
                            <Col lg={24}>
                                <div className="text-soft-blue title-big bold discover-container">
                                Data KMS Anak
                                </div>
                            </Col>
                            <Col lg={24}>
                                <div className="text-black description-discover">
                                Platform pengisian data KMS oleh Admin Posyandu.
                                </div>
                            </Col>
                            <Col lg={24}>
                                <hr style={{
                                    minHeight: 8,
                                    backgroundColor: '#7d5e2a',
                                    border: 'none',
                                    maxWidth: 150,
                                    borderRadius: 26,
                                }}/>
                            </Col>                        
                            <Col lg={24} className="mt-20">
                                <Row
                                    type="flex" justify="center"
                                    className="card-why-container"
                                >
                                    {
                                        whyData.map( data =>
                                            <Col lg={8} md={12} sm={12} className="">
                                                <div className="card-why-content">
                                                    <img src={data.image}  className="card-why-image"/>
                                                    <div className="text-soft-blue semi-bold mt-10">
                                                        {data.title}
                                                    </div>
                                                    <div className="mt-10" style={{maxWidth:'260px'}}>
                                                        {data.description}<br/><br/>
                                                        <a href={data.link}>
                                                            <ButtonHome
                                                                text="More"
                                                                background="#7e6752"
                                                                textColor="#fff"
                                                                className='button-participate'
                                                                onClick = {data.link}
                                                            />
                                                        </a>
                                                    </div>
                                                </div>
                                            </Col>
                                        )
                                    }
                                </Row>
                            </Col>
                        </Row>
                        <Row>
                            <div className="container-title-event">
                                <span>Waiting List</span>
                            </div>
                        </Row>
                        <Table loading={initialData.loading}>
                            <Row gutter={24} type="flex">
                                <Table>
                                    columns={columns}
                                    dataSource={data}
                                    className="table-active-event"
                                </Table>
                            </Row>
                        </Table>
                </Content>
            </Layout>
        );
    }
}

export default KmsComponent;
