import React, {Component} from 'react'; 
import KmsComponent from '../modules/kms-component'
import axios from 'axios';

class KmsPage extends Component {
    state ={
        visible: false,
        anak:[]
    }
   
    componentDidMount(){
        this.getAnak();
    }

    getAnak = () => {
      axios.get(`https://kms-backend.herokuapp.com/`)
      .then(res => {
        // const persons = res.data;
        console.log(res);
        // this.setState({ berita: res.data.articles});
      })
    }

    editAnak = () => {

    }

    deleteAnak = () => {

    }

    

    showModal = () => {
        this.setState({
          visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };
    
      handleCancel = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };
    //handleOk dan handleCancel digunakan untuk close modal

    render(){
    
      const columns = [{
        title: 'Nama Anak',
        dataIndex: 'nama_anak',
        key: 'nama_anak',
        render: text => <a href="#">{text}</a>,
      }, {
        title: 'Usia',
        dataIndex: 'usia_anak',
        key: 'usia_anak',
      },
      {
        title: 'Berat Badan',
        dataIndex: 'bb',
        key: 'bb',
      }, {
        title: 'Tinggi Badan',
        dataIndex: 'tb',
        key: 'tb',
      }, {
        title: 'Instansi',
        dataIndex: 'instansi',
        key: 'instansi',
      }, {
        title: 'Lokasi',
        dataIndex: 'lokasi',
        key: 'lokasi',
      },{
        title: 'Tanggal',
        dataIndex: 'tanggal',
        key: 'tanggal',
      }, {
        title: 'Action',
        key: 'action',
        render: (text, record) => (
          <span>
            <a href="#">Edit</a>
            <span className="ant-divider" />
            <a href="#">Delete</a>
            <span className="ant-divider" />
            <a href="#" className="ant-dropdown-link">
              {/* More actions <Icon type="down" /> */}
            </a>
          </span>
        ),
    }];
    
    const anak = [{
        key: '1',
        nama_anak: 'John Brown',
        usia_anak: 3,
        bb: 10,
        tb: 120,
        instansi: 'Puskesmas Danurejan I',
        lokasi: 'Danurejan',
        tanggal: '12-12-2019'
      }, {
        key: '1',
        nama_anak: 'Winsie Wiradi',
        usia_anak: 3,
        bb: 20,
        tb: 120,
        instansi: 'Puskesmas Danurejan I',
        lokasi: 'Danurejan',
        tanggal: '12-12-2019'
      }, {
        key: '1',
        nama_anak: 'Malika Brown',
        usia_anak: 2,
        bb: 10,
        tb: 100,
        instansi: 'Puskesmas Danurejan I',
        lokasi: 'Danurejan',
        tanggal: '12-12-2019'
      }, 
    ];
    
      return (
            <KmsComponent
                initialData = {this.state} //ini maksudnya membuat varibel initalData yg isinya semua state yang telah dibuat
                showModal = {this.showModal} //ini maksudnya membuat variabel showmodal yang akan dipanggil, this.showmodal mksdnya itu manggil functin show modal, karena masih satu halaman pakainya this.
                handleCancel = {this.handleCancel} //sama seperti showmodal
                handleOk = {this.handleOk} //sama seperti showmodal
                columns = {columns}
                data={anak}
            />
        );
    }
}

export default KmsPage;
