import React, { Component } from 'react'; 
import { Layout, Row, Col, Input, Table } from 'antd'; 
import '../assets/css/landing.css' 

const { Content } = Layout; 
const { Search } = Input;

class LandingComponent extends Component{
    render(){
        const{data_makanan}=this.props;
        const image1 = require(`../assets/images/picture.jpg`); 
        const columns = [{
            title: 'Jumlah',
            dataIndex: 'jml_ingr',
            key: 'jml_ingr',
          }, {
            title: 'Ukuran',
            dataIndex: 'ukuran_ingr',
            key: 'ukuran_ingr',
          },{
            title: 'Makanan',
            dataIndex: 'makanan_ingr',
            key: 'makanan_ingr',
          },{
            title: 'Nutrisi',
            dataIndex: 'nutrisi_ingr',
            key: 'nutrisi_ingr',
          },
        ]
        const data = [{
            jml_ingr : '1',
            ukuran_ingr : 'satuan',
            makanan_ingr : 'pizza',
            nutrisi_ingr : "Energy: 406 kcal <br> Protein: 8 g <br> Fat: 21 g <br> Carbs: 45 g"
        },
        ]

        return(
            <Layout className="landing-container">
                {/*<Navbar/>  {}*/}
                <Content style={{ overflow: "hidden" }}> {}
                    <Row className="section-container" style={{marginBottom:"0px"}}>
                        <Col lg={12} md={12} sm={12}>
                            <div className="image-big-container">
                                <img
                                    src={image1}
                                    alt="Home 1"
                                    style={{maxWidth: '80%', marginLeft:"100px"}}
                                />
                            </div>
                        </Col>
                        <Col lg={12} md={12} sm={12}>
                            <Row>
                                <Col span={24}>
                                    <div className="title-container">
                                        <span className="text-soft-blue title-big bold">Food Calculator</span>
                                    </div>
                                </Col>
                                <Col span={24}>
                                    <div className="image-hidden">
                                        <img
                                            src={image1}
                                            alt="Home 1"
                                            style={{maxWidth: '100%'}}
                                        />
                                    </div>
                                    <div className="description-container desc-medium">
                                    Cari makanan untuk melihat detail gizinya!
                                    </div>
                                </Col>
                                <Col span={24}>
                                    <div className="button-section-1-container">                                
                                        <Search
                                        placeholder="Masukkan Nama Makanan"
                                        onSearch={value => console.log(value)}
                                        style={{ width: 320 }}
                                        />
                                    </div>
                                </Col>
                            </Row>
                        </Col>  
                    </Row>
                    {
                        data.map( data_makanan =>
                            <Row className="section-container">                
                                <Col lg={24} className="mt-20">
                                    <br></br>
                                    <br></br>
                                    <Table columns={columns} dataSource={data_makanan} />
                                </Col>
                            </Row>
                        )
                    }
                </Content>

            </Layout>
        );
    }
}

export default LandingComponent;
