import React, { Component } from 'react'; 
import { Layout, Row, Col, Table, Modal,Select,Button, Input, Icon, message, Divider, Tooltip} from 'antd'; 
import '../assets/css/portofolio.css' 
import Navbar from '../common/layout/navbar-landing' 
import ButtonHome from '../common/component/button/button-home'; 
import ButtonDashboard from '../../../common/component/button/button-dashboard';
import 'moment-timezone';
import 'moment/locale/id';
import moment from 'moment-timezone';
import { faPaperPlane, faTrashAlt } from '@fortawesome/free-solid-svg-icons'
import { faInfoCircle } from '@fortawesome/free-solid-svg-icons'

const { Content } = Layout; // membuat konstanta content 
const { Option } = Select;
const dateNow = moment().format('YYYY-MM-DD');
const { confirm } = Modal;

class KmsComponent extends Component{
    render(){
        const whyData = [
            {
                title: 'No',
                dataIndex: 'no',
                key: 'no',
                fixed: 'left',
                width: 100,
                render: text => <a>{text}</a>,
                sorter: (a, b) => a.no - b.no,
                sortDirections: ['ascend','descend'],
            },
            {
                title: 'Nama Anak',
                dataIndex: 'nama_anak',
                key: 'nama_anak',
                width: 100,
                fixed: 'left',
                ...this.getColumnSearchProps('nama_anak'),
            },
            {
                title: 'Berat Badan',
                dataIndex: 'bb',
                key: 'bb',
                ...this.getColumnSearchProps('bb'),
            },
            {
                title: 'Tinggi Badan',
                dataIndex: 'tb',
                key: 'tb',
                ...this.getColumnSearchProps('tb'),
            },
            {
                title: 'Instansi',
                dataIndex: 'instansi',
                key: 'instansi',
                ...this.getColumnSearchProps('instansi'),
            },
            {
                title: 'Lokasi',
                dataIndex: 'lokasi',
                key: 'lokasi',
                ...this.getColumnSearchProps('lokasi'),
            },
            {
                title: 'Action',
                key: 'action',
                width: 100,
                render: (data) => (
                  [
                  <Row>
                      <Col lg={8} md={24} sm={24} style={data.end_registration < dateNow ? {display:"none"}:{display:"block"}}>
                        <Tooltip title="Kirim">
                            <ButtonDashboard
                                height={20}
                                icon={faPaperPlane}
                                borderRadius="5px"
                                background="#36FF03"
                                onClick = {() => this.showSendConfirm(data.instansi)}
                            />
                        </Tooltip>
                      </Col>
                      <Col lg={8} md={24} sm={24}>
                          <Tooltip title="Detail">
                            <a href={`${data.sertif_URL}`} download>
                              <ButtonDashboard
                                  height={20}
                                  icon={faInfoCircle}
                                  borderRadius="5px"
                                  background="#FFA903"
                                  marginRight="4px"
                              />
                            </a>
                          </Tooltip>
                      </Col>
                    </Row>
                  ]
                ),
              }
        ];
        const { initialData, data, columns } = this.props;
        return(
            <Layout className="landing-container">
                <Content style={{ 
                    overflow: "hidden",
                    padding:15,
                    minHeight:280,
                    borderRadius:"8px",
                    }}
                > 
                    <Row className="section-container">
                            <Col lg={24}>
                                <div className="text-soft-blue title-big bold discover-container">
                                Data KMS Anak
                                </div>
                            </Col>
                            <Col lg={24}>
                                <div className="text-black description-discover">
                                Platform pengisian data KMS oleh Admin Posyandu.
                                </div>
                            </Col>
                            <Col lg={24}>
                                <hr style={{
                                    minHeight: 8,
                                    backgroundColor: '#7d5e2a',
                                    border: 'none',
                                    maxWidth: 150,
                                    borderRadius: 26,
                                }}/>
                            </Col>                        
                            <Col lg={24} className="mt-20">
                                <Row
                                    type="flex" justify="center"
                                    className="card-why-container"
                                >
                                    {
                                        whyData.map( data =>
                                            <Col lg={8} md={12} sm={12} className="">
                                                <div className="card-why-content">
                                                    <img src={data.image}  className="card-why-image"/>
                                                    <div className="text-soft-blue semi-bold mt-10">
                                                        {data.title}
                                                    </div>
                                                    <div className="mt-10" style={{maxWidth:'260px'}}>
                                                        {data.description}<br/><br/>
                                                        <a href={data.link}>
                                                            <ButtonHome
                                                                text="More"
                                                                background="#7e6752"
                                                                textColor="#fff"
                                                                className='button-participate'
                                                                onClick = {data.link}
                                                            />
                                                        </a>
                                                    </div>
                                                </div>
                                            </Col>
                                        )
                                    }
                                </Row>
                            </Col>
                        </Row>
                        <Row>
                            <div className="container-title-event">
                                <span>Waiting List</span>
                            </div>
                        </Row>
                        <Table loading={initialData.loading}>
                            <Row gutter={24} type="flex">
                                <Table>
                                    columns={columns}
                                    dataSource={data}
                                    className="table-active-event"
                                </Table>
                            </Row>
                        </Table>
                </Content>
            </Layout>
        );
    }
}

export default KmsComponent;
