import React, { Component } from 'react'; 
import { Layout, Row, Col, Table, Modal,Select,Button, Input, Icon, message, Divider, Tooltip} from 'antd'; 
import '../assets/css/portofolio.css' 
import Navbar from '../common/layout/navbar-landing' 
import ButtonHome from '../common/component/button/button-home'; 

const { Content } = Layout; // membuat konstanta content 

class KmsComponent extends Component{
    render(){
        const columns = [{
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
            render: text => <a href="#">{text}</a>,
          }, {
            title: 'Age',
            dataIndex: 'age',
            key: 'age',
          }, {
            title: 'Address',
            dataIndex: 'address',
            key: 'address',
          }, {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
              <span>
                <a href="#">Action ä¸€ {record.name}</a>
                <span className="ant-divider" />
                <a href="#">Delete</a>
                <span className="ant-divider" />
                <a href="#" className="ant-dropdown-link">
                  {/* More actions <Icon type="down" /> */}
                </a>
              </span>
            ),
        }];

        const data = [{
            key: '1',
            name: 'John Brown',
            age: 32,
            address: 'New York No. 1 Lake Park',
          }, {
            key: '2',
            name: 'Jim Green',
            age: 42,
            address: 'London No. 1 Lake Park',
          }, {
            key: '3',
            name: 'Joe Black',
            age: 32,
            address: 'Sidney No. 1 Lake Park',
          }];

        return(
            <Layout className="landing-container">
                <Content style={{ 
                    overflow: "hidden",
                    padding:15,
                    minHeight:280,
                    borderRadius:"8px",
                    }}
                > 
                    <Row className="section-container">
                            <Col lg={24}>
                                <div className="text-soft-blue title-big bold discover-container">
                                Data KMS Anak
                                </div>
                            </Col>
                            <Col lg={24}>
                                <div className="text-black description-discover">
                                Platform pengisian data KMS oleh Admin Posyandu.
                                </div>
                            </Col>
                            <Col lg={24}>
                                <hr style={{
                                    minHeight: 8,
                                    backgroundColor: '#7d5e2a',
                                    border: 'none',
                                    maxWidth: 150,
                                    borderRadius: 26,
                                }}/>
                            </Col>                    
                            <Col lg={24} className="mt-20">
                                <Table columns={columns} dataSource={data} />
                            </Col>
                        </Row>
                </Content>
            </Layout>
        );
    }
}

export default KmsComponent;
