import React, {Component, useEffect} from 'react'; 
import LandingComponent from '../modules/landing-component'

import axios from 'axios';

class LandingPage extends Component {
    state ={
        visible: false,
    }

    componentDidMount(){
      this.getGizi(pizza);
    }

    getGizi=(ingr)=>{
      axios.get(`https://api.edamam.com/api/food-database/parser?ingr=${ingr}&app_id=897d1eb6&app_key=78e388625db412c233349cab2c3f68c6`)
      .then(res => {
        console.log(res.data.hints);
        this.setState({ makanan : res.data.hints.food})
      })
    };

    showModal = () => {
        this.setState({
          visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };

      handleCancel = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };

    render(){
        const data =  this.state.makanan.map(data => ({
            author: data.author,
            title : data.title,
            urlToImage : data.urlToImage,
            description : data.description,
            publishedAt : data.publishedAt,
            content : data.content,
        }))
        return (
            <LandingComponent
                initialData = {this.state} 
                showModal = {this.showModal} 
                handleCancel = {this.handleCancel} 
                handleOk = {this.handleOk} 
            />
        );
    }
}

export default LandingPage;
