import React, {Component} from 'react'; 
import KmsComponent from '../modules/kms-component'
import axios from 'axios';
import { message } from 'antd';

class KmsPage extends Component {
    state ={
        visible: false,
        anak:[],
        name : '',
        age : '',
        weight : '',
        height: '',
        institution : '',
        location : '',
    }
   
    componentDidMount(){
        this.getAnak();
    }

    getAnak = () => {
      axios.get(`https://kms-backend.herokuapp.com/`)
      .then(res => {
        // const persons = res.data;
        console.log(res.data.data);
        this.setState({anak: res.data.data});
      })
      // axios.get(`http://newsapi.org/v2/top-headlines?country=id&category=health&apiKey=67ad75d27dcc4815b2c1925aa55d7b4f`)
      // .then(res => {
      //   // const persons = res.data;
      //   console.log(res.data.articles);
      //   this.setState({ anak: res.data.articles});
      // })
    }

    handleChange = (e) => {
      let target = e.target.name;
      let value = e.target.value;
      this.setState({
          [target]: value
      })
    }

    insertAnak = () =>{
      const params = new FormData()
      params.set('nama_sertifikat',this.state.nama_sertifikat)

      axios.post('https://kms-backend.herokuapp.com/insert')
      .then(res=>{
        if(res.status === 200){
          message.success('Data Anak berhasil Ditambahkan');
        }
      })
    }

    editAnak = () => {

    }

    deleteAnak = () => {

    }

    

    showModal = () => {
        this.setState({
          visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };
    
      handleCancel = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };
    //handleOk dan handleCancel digunakan untuk close modal

    render(){
    
    const columns = [{
        title: 'Nama Anak',
        dataIndex: 'nama_anak',
        key: 'nama_anak',
        render: text => <a href="#">{text}</a>,
      }, {
        title: 'Usia',
        dataIndex: 'usia_anak',
        key: 'usia_anak',
      },
      {
        title: 'Berat Badan',
        dataIndex: 'bb',
        key: 'bb',
      }, {
        title: 'Tinggi Badan',
        dataIndex: 'tb',
        key: 'tb',
      }, {
        title: 'Instansi',
        dataIndex: 'instansi',
        key: 'instansi',
      }, {
        title: 'Lokasi',
        dataIndex: 'lokasi',
        key: 'lokasi',
      },{
        title: 'Tanggal',
        dataIndex: 'tanggal',
        key: 'tanggal',
      }, {
        title: 'Action',
        key: 'action',
        render: (text, record) => (
          <span>
            <a href="#">Edit</a>
            <span className="ant-divider" />
            <a href="#">Delete</a>
            <span className="ant-divider" />
            <a href="#" className="ant-dropdown-link">
              {/* More actions <Icon type="down" /> */}
            </a>
          </span>
        ),
    }];
    
    const anak = this.state.anak.map(data => ({
        nama_anak : data.name,
        usia_anak : data.age,
        bb : data.weight,
        tb : data.height,
        instansi : data.institution,
        lokasi : data.location,
        tanggal : data.date,
    }))

  
    
      return (
            <KmsComponent
                initialData = {this.state} //ini maksudnya membuat varibel initalData yg isinya semua state yang telah dibuat
                showModal = {this.showModal} //ini maksudnya membuat variabel showmodal yang akan dipanggil, this.showmodal mksdnya itu manggil functin show modal, karena masih satu halaman pakainya this.
                handleCancel = {this.handleCancel} //sama seperti showmodal
                handleOk = {this.handleOk} //sama seperti showmodal
                columns = {columns}
                anak={anak}
                handleChange = {this.handleChange}
            />
        );
    }
}

export default KmsPage;
